DROP TABLE USUARIOS;CREATE TABLE USUARIOS(	USERNAME TEXT PRIMARY KEY NOT NULL,	PASSWORD TEXT,	CARTAS TEXT,	MONEDAS INTEGER,	SIN_STAMINA TEXT);CREATE TABLE VENTAS(	ID_CARTA INTEGER NOT NULL,	USERNAME TEXT NOT NULL,	PRECIO INTEGER,	FECHA_HORA TEXT NOT NULL,	PRIMARY KEY (USERNAME, FECHA_HORA)	FOREIGN KEY (ID_CARTA) REFERENCES MODELO_CARTAS(ID),	FOREIGN KEY (USERNAME) REFERENCES USUARIOS(USERNAME));

